    for(var i=0;i<links.length;i++){
        if(!links[i])continue;
        var link = links[i].trim();
        if(!(link.startsWith('#')||link.startsWith('javascript')||link.startsWith('void('))){
            try{
                var the_url = url.resolve(pageurl,link);
                if(the_url!=pageurl)cleaned_link.push(the_url);
            }catch(e){
                logger.error('Url resolve error: '+pageurl+', '+link);
            }

        }
    }
    return arrayUnique(cleaned_link);
}
/**
 * detect link which drill rule matched
 * @param link
 * @returns [alias name,alias]
 */
extractor.prototype.detectLink = function(link){
    var urlobj = url.parse(link);
    var result = [];
    var domain = this.__getTopLevelDomain(urlobj['hostname']);
    if(domain && this.spiderCore.spider.driller_rules[domain]!=undefined){
        var alias = this.spiderCore.spider.driller_rules[domain];
        for(a in alias){
            var url_pattern  = decodeURIComponent(alias[a]['url_pattern']);
            var patt = new RegExp(url_pattern);
            if(patt.test(link)){
                result = ['driller:'+domain+':'+a,alias[a]];
                break;
            }
        }

    }
    return result;
}

/**
 * arrange link array.
 * @param links
 * @returns {{}}
 */
extractor.prototype.arrange_link = function(links){
    var linkobj = {};
    for(var i=0;i<links.length;i++){
        var link = links[i];
        var matched_driller = this.detectLink(link);
        if(matched_driller.length>0){
            var driller_lib = 'urllib:' + matched_driller[0];
            var driller_rule = matched_driller[1];
            if(typeof(driller_rule)!='object')driller_rule = JSON.parse(driller_rule);
            if(linkobj[driller_lib]==undefined)linkobj[driller_lib]=[];
            if(driller_rule['id_parameter']&&driller_rule['id_parameter'].length>0){
                var id_parameter = driller_rule['id_parameter'];
                var urlobj = url.parse(link);
                var parameters = querystring.parse(urlobj.query);
                var new_parameters = {};
                for(var x=0;x<id_parameter.length;x++){
                    var param_name = id_parameter[x];
                    if(x==0&&param_name=='#')break;
                    if(parameters.hasOwnProperty(param_name))new_parameters[param_name] = parameters[param_name];
                }
                urlobj.search = querystring.stringify(new_parameters);
                link = url.format(urlobj);
            }
            linkobj[driller_lib].push(link);
        }
    }
    for(var i in linkobj){
        if(linkobj.hasOwnProperty(i)){
            linkobj[i] = arrayUnique(linkobj[i]);
        }
    }
    return linkobj;
}


/**
 * generate drill relation string: page->sub page->sub page
 * @param crawl_info
 * @returns string
 */
extractor.prototype.getDrillRelation = function($,crawl_info){
    //var rule = crawl_info['origin']['drill_relation_rule'];//rule: {"base":"content","mode":"css","expression":"#breadCrumb","pick":"innerText","index":1}
    var rule = this.spiderCore.spider.getDrillerRule(crawl_info['origin']['urllib'],'drill_relation');
    var origin_relation = crawl_info['origin']['drill_relation'];
    if(!origin_relation)origin_relation = '*';
    var new_relation = '*';
    if(rule){
        switch(rule['mode']){
            case 'regex':
                if(rule['base']==='url'){
                    new_relation = this.regexSelector(crawl_info['url'],rule['expression'],rule['index']);
                }else{
                    new_relation = this.regexSelector(crawl_info['content'],rule['expression'],rule['index']);
                }
                break;
            case 'css':
            default:
                new_relation = this.cssSelector($.root(),rule['expression'],rule['pick'],rule['index']);
                break;
        }
    }
    return util.format('%s->%s',origin_relation,new_relation);
}

/**
 * extractor: for now , just extract links
 * @param crawl_info
 * @returns {*}
 */
extractor.prototype.extract = function(crawl_info){
    if(crawl_info['origin']['format']=='binary')return crawl_info;
    var extract_rule = this.spiderCore.spider.getDrillerRule(crawl_info['origin']['urllib'],'extract_rule');

    if(crawl_info['origin']['drill_rules']||extract_rule['rule']){
        var $ = cheerio.load(crawl_info['content']);
    }

    if(crawl_info['origin']['drill_rules']){
        if(crawl_info['drill_link']){
            var drill_link = crawl_info['drill_link'];
        }else{
            var drill_link = this.extract_link($,crawl_info['origin']['drill_rules']);
        }

        var washed_link = this.wash_link(crawl_info['url'],drill_link);
        crawl_info['drill_link'] = this.arrange_link(washed_link);
        if(this.spiderCore.settings['keep_link_relation'])crawl_info['drill_relation'] = this.getDrillRelation($,crawl_info);
    }

    if(extract_rule['rule']&&!isEmpty(extract_rule['rule'])){
        var extracted_data = this.extract_data(crawl_info['url'],crawl_info['content'],extract_rule,null,$.root());
        crawl_info['extracted_data'] = extracted_data;
    }
    return crawl_info;
}
/**
 * extract data
 * @param url
 * @param content
 * @param extract_rule
 * @param uppper_data
 * @param dom
 * @returns {{}}
 */
extractor.prototype.extract_data = function(url,content,extract_rule,uppper_data,dom){
    var data = {};
    var self = this;
    if(extract_rule['category'])data['$category'] = extract_rule['category'];
//    if(extract_rule['require'])data['$require'] = extract_rule['require'];
    if(extract_rule['relate'])data['relate'] = uppper_data[extract_rule['relate']];
    for(i in extract_rule['rule']){
        if(extract_rule['rule'].hasOwnProperty(i)){
            var rule = extract_rule['rule'][i];
            var baser = content;
            if(rule['base']==='url')baser = url;
            switch(rule['mode']){
                case 'regex':
                    var tmp_result = this.regexSelector(baser,rule['expression'],rule['index']);
                    data[i] = tmp_result;
                    break;
                case 'xpath':
                    break;
                case 'value':
                    data[i] = rule['expression'];
                    break;
                case 'json':
                    break;
                default://css selector
                    if(dom)baser = dom;
                    else baser = (cheerio.load(content)).root();
                    var pick = rule['pick'];
                    if(rule['subset']){
                        pick = false;
                        (function(k){
                            var result_arr = [];
                            var tmp_result = self.cssSelector(baser,rule['expression'],pick,rule['index']);
                            if(tmp_result){
                                tmp_result.each(function(x, elem) {
                                    var sub_dom = tmp_result.eq(x);
                                    result_arr.push(self.extract_data(url,content,rule['subset'],data,sub_dom));
                                });
                            }
                            if(!isEmpty(result_arr))data[k] = result_arr;
                        })(i);
                    }else{
                        try{
                            var tmp_result = this.cssSelector(baser,rule['expression'],pick,rule['index']);
                            if(tmp_result&&!isEmpty(tmp_result))data[i] = tmp_result;
                        } catch(e){
                            logger.error(url + ' extract field '+ i + ' error:'+e);
                        }
                    }

            }
            }
    }
    if(extract_rule['require']){
        var lacks = [];
        for(var c=0;c<extract_rule['require'].length;c++){
            var key = extract_rule['require'][c];
            if(typeof(key)==='object'){
                var sublack = self.checksublack(key,data);
                if(sublack.length>0)lacks = lacks.concat(sublack);
            }else{
                if(!data[key]){
                    lacks.push(key);
                    logger.warn(key + ' not found in '+ url + ' extracted data');
                }
            }
        }
        if(!isEmpty(lacks)){
            logger.error(url + ' extracted data lacks of '+lacks.join(','));
            self.spiderCore.spider.redis_cli2.zadd('incomplete:data:url',(new Date()).getTime(),url,function(err,result){
                //nothing
            });
            if('data_lack_alert' in self.spiderCore.spider_extend)self.spiderCore.spider_extend.data_lack_alert(url,lacks);
        }else{
            self.spiderCore.spider.redis_cli2.zrem('incomplete:data:url',url,function(err,result){
                //nothing
            });
        }
    }
    return data;
}
//check sublack
extractor.prototype.checksublack = function(keys,data){
    var sublackarr = [];
    for(var x=0;x<keys.length;x++){
        if(!data[keys[x]]){
            sublackarr.push(keys[x]);
            logger.warn(keys[x] + ' not found in '+ url + ' extracted data');
        }
    }
    if(sublackarr.length===keys.length)return sublackarr;
    else return [];
}

/**
 * extract value base expression
 * @param $
 * @param expression
 * @param pick
 * @param index
 * @returns {*}
 */
extractor.prototype.cssSelector = function($,expression,pick,index){
//    logger.debug('css expression: '+expression);
    if(!index)index=1;
    var real_index = parseInt(index) - 1;
    //if(real_index<0)real_index=0;
    var tmp_val = $.find(expression);
    if(!pick)return tmp_val;
    if(typeof(tmp_val)==='object'){
        if(real_index>=0){
            var val = tmp_val.eq(real_index);
            return this.cssSelectorPicker(val,pick);
        }else{
            var arrayResult = [];
            for(var i=0;i<tmp_val.length;i++){
                var val = tmp_val.eq(i);
                arrayResult.push(this.cssSelectorPicker(val,pick));
            }
            if(arrayResult.length==1)arrayResult = arrayResult[0];
            return arrayResult;
        }
    }else {
        var val = tmp_val;
        return this.cssSelectorPicker(val,pick);
    }
}
/**
 * pick value/attribute from element
 * @param val
 * @param pick
 * @returns {*}
 */
extractor.prototype.cssSelectorPicker = function(val,pick){
    var result;
    if(pick.startsWith('@')){
        result = val.attr(pick.slice(1));
    }
    else{
        switch(pick.toLowerCase()){
            case 'text':
            case 'innertext':
                result = val.text();
                break;
            case 'html':
            case 'innerhtml':
                result = val.html();
                break;
        }
    }
    //if(result)result = result.replace(/[\r\n\t]/g, "").trim();
    if(result)result = result.trim();
    return result;
}

/**
 * return matched group base expression
 * @param content
 * @param expression
 * @param index
 * @returns {*}
 */
extractor.prototype.regexSelector = function(content,expression,index){
    var index = parseInt(index);
    if(index==0)index=1;
    var expression = new RegExp(expression,"ig");
    if(index>0){
        var matched = expression.exec(content);
        if(matched&&matched.length>index)return matched[index];
    }else{
        var arr = [],matched;
        while (matched = expression.exec(content))
            arr.push(matched[1]);
        return arr;
    }

}

extractor.prototype.validateContent = function(crawl_info){
    var self = this;
    var result = true;
    var statusCode = parseInt(crawl_info['statusCode']);
    var limitation = 500;
    if(crawl_info['origin']['format']=='binary')limitation = 20;
    if(statusCode===200){
        if(crawl_info['content'].length<limitation){
            logger.error(util.format('Too little content: %s, length:%s',crawl_info['url'],crawl_info['content'].length));
            result = false;
        }
        if(crawl_info['origin']['validation_keywords']){
            for(var i =0;i<crawl_info['origin']['validation_keywords'].length;i++){
                var keyword = crawl_info['origin']['validation_keywords'][i];
                if(crawl_info['content'].indexOf(keyword)<0){
                    logger.error(util.format('%s lacked keyword: %s',crawl_info['url'],keyword));
                    result = false;break;
                }
            }
        }
    }else{
        logger.error(util.format('url:%s, status code: %s',crawl_info['url'],statusCode));
        if(statusCode>300)result=false;//30x,40x,50x
    }
    if(self.spiderCore.settings['to_much_fail_exit']){
        self.cumulative_failure +=  result?-1:1
        if(self.cumulative_failure<0)self.cumulative_failure = 0;
        if(self.cumulative_failure>self.spiderCore.settings['spider_concurrency']*1.5){
            logger.fatal('too much fail, exit. '+self.cumulative_failure);
            process.exit(1);
        }
    }
    return result;
}

module.exports = extractor;

/**
 * Created by james on 13-11-22.
 * pipeline middleware
 */
var crypto = require('crypto');
var redis = require("redis");
var HBase = require('../lib/node_hbase/index.js');
var hbase_http = require('hbase');
var os = require("os");
var async = require('async');
var urlUtil =  require("url");
var querystring = require('querystring');
var util = require('util');
var poolModule = require('generic-pool');
require('../lib/jsextend.js');

var pipeline = function(spiderCore){
    this.spiderCore = spiderCore;
    logger = spiderCore.settings.logger;
}

////report to spidercore standby////////////////////////
pipeline.prototype.assembly = function(callback){
    var spiderCore = this.spiderCore;
    this.hbase_via_http = false;
    if(spiderCore.settings['crawled_hbase_conf'] instanceof Array)this.hbase_via_http  = true;
    if(this.spiderCore.settings['save_content_to_hbase']===true){
        if(this.hbase_via_http){
            this.hbase_cli = hbase_http({
                host:this.spiderCore.settings['crawled_hbase_conf'][0],
                port:this.spiderCore.settings['crawled_hbase_conf'][1]
            });
            this.HBASE_TABLE = this.hbase_cli.getTable(this.spiderCore.settings['crawled_hbase_table']);
            this.HBASE_BIN_TABLE = this.hbase_cli.getTable(this.spiderCore.settings['crawled_hbase_bin_table']);
        }else{
            this.HBASE_POOL = poolModule.Pool({
                name     : 'hbase_pool',
                create   : function(hbase_callback) {
                    logger.debug('Create a hbase connection.');
                    hbase_callback(null,HBase(spiderCore.settings['crawled_hbase_conf']));
                },
                destroy  : function(db) {
                    logger.debug('Destroy a hbase connection.');
                    db = null;
                },
                max      : this.spiderCore.settings['spider_concurrency'],
                idleTimeoutMillis : 30000,
                log : false
            });
//            this.hbase_cli = HBase(this.spiderCore.settings['crawled_hbase_conf']);
            this.HBASE_TABLE = this.spiderCore.settings['crawled_hbase_table'];
            this.HBASE_BIN_TABLE = this.spiderCore.settings['crawled_hbase_bin_table'];
        }
    }

    this.redis_cli0 = spiderCore.spider.redis_cli0;
    this.redis_cli1 = spiderCore.spider.redis_cli1;
    this.redis_cli2 = spiderCore.spider.redis_cli2;
    if(callback)callback(null,'done');
}
/**
 * save links to redis db
 * @param page_url
 * @param linkobjs
 */
pipeline.prototype.save_links = function(page_url,version,linkobjs,drill_relation,callback){
    var spiderCore = this.spiderCore;
    var redis_cli0 = this.redis_cli0;
    var redis_cli1 = this.redis_cli1;
    var aliasArr = Object.keys(linkobjs);
    var linkCount = 0;
    var index = 0;
    if(!version)version = (new Date()).getTime();
    async.whilst(
        function () { return index < aliasArr.length; },
        function (cb) {
            var alias = aliasArr[index];
            var links = linkobjs[alias];
            var t_alias_arr = alias.split(':');
            var drill_alias = t_alias_arr[3];
            var domain = t_alias_arr[2];
            if(!spiderCore.spider.driller_rules[domain]||!spiderCore.spider.driller_rules[domain][drill_alias]){
                logger.error(alias+' not in configuration');
                cb(new Error('Drill rule not found'));
            }
            var t_driller_rules = spiderCore.spider.driller_rules[domain][drill_alias];
            if(typeof(t_driller_rules)!='object')t_driller_rules = JSON.parse(t_driller_rules);
            ////////////////////////////////link array/////////////////////////
            var sindex = 0;
            async.whilst(
                function () { return sindex < links.length; },
                function (scb) {
                    var link = links[sindex];
                    linkCount++;
                    /////save a link/////////////////////
                    async.waterfall([
                        //transform link////////////////////////////////
                        function(mcb){
                            var final_link = link;
                            var urlobj = urlUtil.parse(link);
                            if(t_driller_rules['id_parameter']){
                                var id_parameter = t_driller_rules['id_parameter'];
                                if(typeof(id_parameter)!='object')id_parameter = JSON.parse(id_parameter);
                                if(Array.isArray(id_parameter)&&id_parameter.length>0){
                                    var parameters = querystring.parse(urlobj.query);
                                    var new_parameters = {};
                                    for(var x=0;x<id_parameter.length;x++){
                                        var param_name = id_parameter[x];
                                        if(x==0&&param_name=='#')break;
                                        if(parameters.hasOwnProperty(param_name))new_parameters[param_name] = parameters[param_name];
                                    }
                                    urlobj.search = querystring.stringify(new_parameters);
                                    final_link = urlUtil.format(urlobj);
                                }
                            }
                            return mcb(null,final_link);
                        },
                        ////get url info //////////////
                        function(final_link, mcb){
                            if(final_link!=link)logger.debug('Transform: ' + link + ' -> '+final_link);
                            var urlhash = crypto.createHash('md5').update(final_link+'').digest('hex');
                            redis_cli1.hgetall(urlhash,function(err, value){
                                mcb(err,final_link,urlhash,value);
                            });
                        },
                        //check url////////////////////////
                        function(final_link,urlhash,values,mcb){
                            var validate = true;
                            if(values&&!isEmpty(values)){
                                var status = values['status'];
                                var records = values['records']?JSON.parse(values['records']):[];
                                var last = parseInt(values['last']);
                                var t_version = parseInt(values['version']);
                                var type = values['type'];

                                if(status!='crawled_failure'){
                                    var real_interval = t_driller_rules['schedule_interval']*1000;
                                    if(status=='crawling'||status=='schedule'){
                                        real_interval = 10*60*1000;//url request hang up or interrupted, give opportunity to crawl after 10 minutes.
                                    }
                                    if(status=='hit'){
                                        real_interval = 2*24*60*60*1000;//probably schedule lost, give opportunity to crawl after 2 days.
                                    }
                                    if(status=='crawled_finish'&&type=='branch'&&version>last){
                                        real_interval = 0;
                                        logger.debug(final_link +' got new version after last crawling');
                                    }
                                    if((new Date()).getTime()-last<real_interval){
                                        logger.debug(util.format('ignore %s, last event time:%s, status:%s',final_link,last,status));
                                        validate = false;
                                    }else{
                                        logger.debug(final_link+' should insert into urlqueue');
                                    }
                                }
                                //version update////////////////////////////////////////////////////////////////
                                logger.debug('url info exists, '+link+', just update the version');
                                var ctc = {};
                                if(validate)ctc['status'] = 'hit';
                                if(version>t_version||isNaN(t_version)){
                                    ctc['version'] = version;
                                    logger.debug('update url('+final_link+') version, '+t_version+' -> '+version);
                                }else {
                                    logger.debug(final_link+' keep the version: '+values['version']);
                                }
                                if(!isEmpty(ctc)){
                                    redis_cli1.hmset(urlhash,ctc,function(err, svalue){
                                        if(err){logger.error(err);}
                                        logger.debug('update url('+final_link+') version, '+t_version+' -> '+version);
                                        mcb(err,final_link,validate);
                                    });
                                }else mcb(null,final_link,validate);
                                //////////////////////////////////////////////////////////////////////////////
                            }else{
                                var vv = {
                                    'url':link,
                                    'version':version,
                                    'trace':alias,
                                    'referer':page_url,
                                    'create':(new Date()).getTime(),
                                    'records':JSON.stringify([]),
                                    'last':(new Date()).getTime(),
                                    'status':'hit'
                                }
                                if(spiderCore.settings['keep_link_relation']){
                                    vv['drill_relation'] = drill_relation?drill_relation:'*';
                                }
                                redis_cli1.hmset(urlhash,vv,function(err, value){
                                    if (err) throw(err);
                                    logger.debug(' save new url('+link+') to urlinfo.');
                                    mcb(null,final_link,true);
                                });
                            }
                        },
                        ////save link to urllib////////////
                        function(final_link,validate,mcb){
                            if(validate){
                                redis_cli0.rpush(alias,final_link,function(err, value){
                                    if(err)throw(err);
                                    logger.debug('push url: '+link+' to urllib: '+alias);
                                    mcb(err,value);
                                });
                            }else mcb(null,'done');
                        }
                    ], function (err, result) {
                        sindex++;
                        scb(err);
                    });
                    ////////////////////////////////////
                },
                function (err) {
                    index++;
                    cb(err);
                }
            );
            ///////////////////////////////////////////////////////////////////
        },
        function (err) {
            logger.info('save '+linkCount+' links from '+page_url+' to redis');
            if(callback)callback(null,true);
        }
    );
}
/**
 * save content to hbase(default), if pipeline defined in spider_extend, it will be covered.
 * @param pageurl
 * @param content
 * @param referer
 * @param urllib
 */
pipeline.prototype.save_content = function(pageurl,content,extracted_data,js_result,referer,urllib,drill_relation,callback){
    if(this.hbase_via_http)return this.save_content_vhttp(pageurl,content,extracted_data,js_result,referer,urllib,drill_relation,callback);

    var url_hash = crypto.createHash('md5').update(pageurl+'').digest('hex');
    var spider = os.hostname()+'-'+process.pid;
    var start_time = (new Date()).getTime();
    var self = this;

    var put = new HBase.Put(url_hash);
    put.add('basic','spider',spider);
    put.add('basic','url',pageurl);
    put.add('basic','referer',referer);
    put.add('basic','urllib',urllib);
    put.add('basic','updated',(new Date()).getTime().toString());

    if(content&&!isEmpty(content)){
        put.add('basic','content',content);
    }

    if(drill_relation&&!isEmpty(drill_relation)){
        put.add('basic','drill_relation',drill_relation);
    }

    if(extracted_data&&!isEmpty(extracted_data)){
        for(d in extracted_data){
            if(extracted_data.hasOwnProperty(d)&&extracted_data[d]!=undefined){
                put.add('data',d,typeof(extracted_data[d])=='object'?JSON.stringify(extracted_data[d]):extracted_data[d]);
            }
        }
    }

    if(js_result&&!isEmpty(js_result)){
        put.add('data','jsresult',js_result);
    }

    self.HBASE_POOL.acquire(function(err, db) {
        if(err){
            self.HBASE_POOL.release(db);
            logger.error(pageurl+', connect to hbase error: '+err);
            self.redis_cli2.zadd('stuck:'+urllib,(new Date()).getTime(),pageurl,function(err,result){
                if(err)throw err;
                logger.debug('append '+pageurl+' to stuck record');
                if(callback)callback(err);
            });
        } else {
            db.put(extracted_data['$category']||this.HBASE_TABLE,put, function (err,res) {
                self.HBASE_POOL.release(db);
                if(err){
                    logger.error(pageurl+', data insert to hbase error: '+err);
                    self.redis_cli2.zadd('stuck:'+urllib,(new Date()).getTime(),pageurl,function(err,result){
                        if(err)throw err;
                        logger.debug('append '+pageurl+' to stuck record');
                        if(callback)callback(err);
                    });
                }else{
                    logger.info(pageurl+', data insert to hbase cost '+((new Date()).getTime()-start_time)+' ms');
                    self.redis_cli2.zrem('stuck:'+urllib,pageurl,function(err,result){
                        if(err)throw err;
                        logger.debug('remove '+pageurl+' from stuck record');
                        if(callback)callback(err);
                    });
                }
            });
        }
    });
}
/**
 * save content via http
 * @param pageurl
 * @param content
 * @param extracted_data
 * @param js_result
 * @param referer
 * @param urllib
 * @param drill_relation
 * @param callback
 */
pipeline.prototype.save_content_vhttp = function(pageurl,content,extracted_data,js_result,referer,urllib,drill_relation,callback){
    var url_hash = crypto.createHash('md5').update(pageurl+'').digest('hex');
    var spider = os.hostname()+'-'+process.pid;
    var start_time = (new Date()).getTime();
    var self = this;

//    var val_cells = [
//                { "column":"basic:spider","timestamp":Date.now(),"$":spider},
//                { "column":"basic:url","timestamp":Date.now(),"$":pageurl},
//                { "column":"basic:content","timestamp":Date.now(),"$":content}
//                ];
    var keylist = ['basic:spider','basic:url','basic:updated'];
    var valuelist = [spider,pageurl,(new Date()).getTime().toString()];

    if(referer&&!isEmpty(referer)){
        keylist.push('basic:referer');
        valuelist.push(referer);
    }

    if(urllib&&!isEmpty(urllib)){
        keylist.push('basic:urllib');
        valuelist.push(urllib);
    }

    if(drill_relation&&!isEmpty(drill_relation)){
        keylist.push('basic:drill_relation');
        valuelist.push(drill_relation);
    }

    if(content&&!isEmpty(content)){
        keylist.push('basic:content');
        valuelist.push(content);
    }

    if(extracted_data&&!isEmpty(extracted_data)){
        for(d in extracted_data){
            if(extracted_data.hasOwnProperty(d)&&extracted_data[d]!=undefined){
                keylist.push('data:'+d);
                valuelist.push(typeof(extracted_data[d])=='object'?JSON.stringify(extracted_data[d]):extracted_data[d]);
            }
        }
//            keylist.push('basic:data');
//            valuelist.push(JSON.stringify(extracted_data));
    }
    if(js_result&&!isEmpty(js_result)){
        keylist.push('data:jsresult');
        valuelist.push(js_result);
    }
    var row = this.HBASE_TABLE.getRow(url_hash);
    try{
        row.put(keylist,valuelist,function(err,success){
            logger.info('insert content extracted from '+pageurl+', cost: '+((new Date()).getTime() - start_time)+' ms');
            if(err){
                logger.error(pageurl+', data insert to hbase error: '+err);
                self.redis_cli2.zadd('stuck:'+urllib,(new Date()).getTime(),pageurl,function(err,result){
                    if(err)throw err;
                    logger.debug('append '+pageurl+' to stuck record');
                    if(callback)callback(err);
                });
            }else{
                logger.info(pageurl+', data insert to hbase cost '+((new Date()).getTime()-start_time)+' ms');
                self.redis_cli2.zrem('stuck:'+urllib,pageurl,function(err,result){
                    if(err)throw err;
                    logger.debug('remove '+pageurl+' from stuck record');
                    if(callback)callback(err);
                });
            }
        });
//        row.put(['basic:spider','basic:url','basic:content'],[spider,pageurl,content],function(err,success){
//            logger.debug('insert content extracted from '+pageurl);
//        });
    }catch(e){
        logger.error(pageurl+', data insert to hbase error: '+e);
        self.redis_cli2.zadd('stuck:'+urllib,(new Date()).getTime(),pageurl,function(err,result){
            if(err)throw err;
            logger.debug('append '+pageurl+' to stuck record');
            if(callback)callback(err);
        });
    }
}

pipeline.prototype.save_binary = function(pageurl,content,referer,urllib,drill_relation,callback){
    if(this.hbase_via_http)return this.save_binary_vhttp(pageurl,content,referer,urllib,drill_relation,callback);

    var url_hash = crypto.createHash('md5').update(pageurl+'').digest('hex');
    var spider = os.hostname()+'-'+process.pid;
    var start_time = (new Date()).getTime();
    var self = this;

    var put = new HBase.Put(url_hash);
    put.add('basic','spider',spider);
    put.add('basic','url',pageurl);
    put.add('binary','file',content);
    put.add('basic','referer',referer);
    put.add('basic','urllib',urllib);
    put.add('basic','updated',(new Date()).getTime().toString());

    self.HBASE_POOL.acquire(function(err, db) {
        if(err) {
            self.HBASE_POOL.release(db);
            logger.error(pageurl+', data connect to hbase error: '+err);
            self.redis_cli2.zadd('stuck:'+urllib,(new Date()).getTime(),pageurl,function(err,result){
                if(err)throw err;
                logger.debug('append '+pageurl+' to stuck record');
                if(callback)callback(err);
            });
        }else{
            db.put(this.HBASE_BIN_TABLE, put, function (err,res) {
                self.HBASE_POOL.release(db);
                if(err){
                    logger.error(pageurl+', data insert to hbase error: '+err);
                    self.redis_cli2.zadd('stuck:'+urllib,(new Date()).getTime(),pageurl,function(err,result){
                        if(err)throw err;
                        logger.debug('append '+pageurl+' to stuck record');
                        if(callback)callback(err);
                    });
                }else{
                    logger.info(pageurl+', data insert to hbase cost '+((new Date()).getTime()-start_time)+' ms');
                    self.redis_cli2.zrem('stuck:'+urllib,pageurl,function(err,result){
                        if(err)throw err;
                        logger.debug('remove '+pageurl+' from stuck record');
                        if(callback)callback(err);
                    });
                }
            });
        }
    });
}

/**
 * save binary to hbase via http
 * @param pageurl
 * @param content
 * @param referer
 * @param urllib
 * @param drill_relation
 * @param callback
 */
pipeline.prototype.save_binary_vhttp = function(pageurl,content,referer,urllib,drill_relation,callback){
    var url_hash = crypto.createHash('md5').update(pageurl+'').digest('hex');
    var spider = os.hostname()+'-'+process.pid;
    var start_time = (new Date()).getTime();
    var self = this;

    var keylist = [
        'basic:spider',
        'basic:url',
        'basic:updated'
    ];
    var valuelist = [
        spider,
        pageurl,
        (new Date()).getTime().toString()
    ];

    if(referer){
        keylist.push('basic:referer');
        valuelist.push(referer);
    }

    if(urllib){
        keylist.push('basic:urllib');
        valuelist.push(urllib);
    }

    if(drill_relation){
        keylist.push('basic:drill_relation');
        valuelist.push(drill_relation);
    }

    if(content){
        keylist.push('binary:file');
        valuelist.push(content);
    }

    var row = this.HBASE_BIN_TABLE.getRow(url_hash);
    try{
        row.put(keylist,valuelist,function(err,success){
            logger.info('insert content extracted from '+pageurl+', cost: '+((new Date()).getTime() - start_time)+' ms');
            if(err){
                logger.error(pageurl+', data insert to hbase error: '+err);
                self.redis_cli2.zadd('stuck:'+urllib,(new Date()).getTime(),pageurl,function(err,result){
                    if(err)throw err;
                    logger.debug('append '+pageurl+' to stuck record');
                    if(callback)callback(err);
                });
            }else{
                logger.info(pageurl+', data insert to hbase cost '+((new Date()).getTime()-start_time)+' ms');
                self.redis_cli2.zrem('stuck:'+urllib,pageurl,function(err,result){
                    if(err)throw err;
                    logger.debug('remove '+pageurl+' from stuck record');
                    if(callback)callback(err);
                });
            }
        });
    }catch(e){
        logger.error(pageurl+', data insert to hbase error(2): '+e);
        self.redis_cli2.zadd('stuck:'+urllib,(new Date()).getTime(),pageurl,function(err,result){
            if(err)throw err;
            logger.debug('append '+pageurl+' to stuck record');
            if(callback)callback(err);
        });
    }
}

/**
 * pipeline save entrance
 * @param extracted_info
 */
pipeline.prototype.save =function(extracted_info,callback){
    var pipeline = this;
    if(this.spiderCore.settings['test']){
        var fs = require('fs');
        var path = require('path');
        if(extracted_info['origin']['format']=='binary'){
            var dumpfile = path.join(__dirname,'..', 'instance',this.spiderCore.settings['instance'],'logs','dumpfile.jpg');
            fs.writeFile(dumpfile,extracted_info['content'],'utf8',function(err){
                if (err)throw err;
                logger.debug('Crawling file saved, '+dumpfile);
                if(callback)callback(true);
            });
        }else{
            var htmlfile = path.join(__dirname,'..', 'instance',this.spiderCore.settings['instance'],'logs','debug-page.html');
            var resultfile = path.join(__dirname,'..', 'instance',this.spiderCore.settings['instance'],'logs','debug-result.json');
            fs.writeFile(htmlfile,extracted_info['content'],'utf8', function (err) {
                if (err)throw err;
                logger.debug('Content saved, '+htmlfile);
                delete extracted_info['content'];
                fs.writeFile(resultfile,JSON.stringify(extracted_info),'utf8',function(err){
                    if (err)throw err;
                    logger.debug('Crawling result saved, '+resultfile);
                    if(callback)callback(true);
                    else process.exit(0);
                });
            });
        }
    }else{
        async.series([
                function(cb){
                    if(extracted_info['drill_link'])pipeline.save_links(extracted_info['url'],extracted_info['origin']['version'],extracted_info['drill_link'],extracted_info['drill_relation'],cb);
                    else cb(null);
                },
                function(cb){
                    if(pipeline.spiderCore.settings['save_content_to_hbase']===true&&extracted_info['origin']['type']=='node'){//type must be node
                        if(extracted_info['origin']['format']=='binary'){
                            pipeline.save_binary(extracted_info['url'],extracted_info['content'],extracted_info['origin']['referer'],extracted_info['origin']['urllib'],extracted_info['drill_relation'],cb);
                        }else{
                            var html_content = extracted_info['content'];
                            if(!extracted_info['origin']['save_page'])html_content = false;
                            pipeline.save_content(
                                extracted_info['url'],
                                html_content,
                                extracted_info['extracted_data'],
                                extracted_info['js_result'],
                                extracted_info['origin']['referer'],
                                extracted_info['origin']['urllib'],
                                extracted_info['drill_relation'],
                                function(s_err){
					                if(!s_err && 'save_content_alert' in pipeline.spiderCore.spider_extend)pipeline.spiderCore.spider_extend.save_content_alert(extracted_info);//report
					                cb(s_err);
				            });
                        }
                    }else cb(null);
                },
                function(cb){
                    if('pipeline' in pipeline.spiderCore.spider_extend)pipeline.spiderCore.spider_extend.pipeline(extracted_info,cb);//spider extend
                    else cb(null);
                }
             ],
            function(err, results){
                logger.info(extracted_info['url']+', pipeline completed');
                callback(err);
            });
    }
}

module.exports = pipeline;

