        {
                redis_cli.hlist(options['q'],function(err,keyList){
                    for(var i=0;i<keyList.length;i++){}
                    var count = 0;
                    if(fs.existsSync(options['f']))fs.unlinkSync(options['f']);
                    async.whilst(
                        function () { return count < keyList.length; },
                        function (callback) {
                            var key = keyList[count];
