page.onResourceReceived = function (res) {
    if (res.stage === 'start') {
    }
    else if (res.stage === 'end') {
        if(res.url===page.url){
            page.status = res.status;
            for(var s=0;s<res.headers.length;s++){
                if(res.headers[s]['name']==='remoteproxy'){
                    page.remoteProxy = res.headers[s]['value'];
