};

page.onLoadFinished = function(status) {
    if (status !== 'success') {
        sendToCaller({'signal':CMD_SIGNAL_CRAWL_FAIL,'message':'Open page failed',"url":page.url});
    } else {
        page.endTime = new Date();
        workAfterLoadFinish(0,0);
    }

};

